mapper.select:
select * from 
     (select rowNum rNum,e_no,e_title,e_author,e_publisher,e_publication_year,e_introduce,e_recommend,e_status,e_group
     from (select * from ebook






      e_title like #{find_name} 


      e_author like #{find_name}


      e_publisher like #{find_name}

     AND e_group = #{find_theme} 

      order by e_no desc))
     where rNum >= #{startrow} and rNum <= #{endrow}
